import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

const Dashboard = ({ user }) => {
  const [airQualityData, setAirQualityData] = useState([]);
  const [chartData, setChartData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [hasHVAC, setHasHVAC] = useState(false);
  const [hasEcologica, setHasEcologica] = useState(false);
  const [city, setCity] = useState('Toronto');
  const [firstName, setFirstName] = useState('');

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day',
          displayFormats: {
            day: 'MMM d'
          }
        },
        title: {
          display: true,
          text: 'Date'
        }
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'µg/m³'
        }
      }
    },
    plugins: {
      legend: {
        position: 'top',
      }
    }
  };

  useEffect(() => {
    if (airQualityData.length > 0) {
      const pm25Data = {
        labels: airQualityData.map(d => new Date(d.timestamp)),
        datasets: [{
          label: 'PM2.5',
          data: airQualityData.map(d => parseFloat(d.pm25)),
          borderColor: 'rgb(75, 192, 192)',
          tension: 0.1
        }]
      };

      const pm10Data = {
        labels: airQualityData.map(d => new Date(d.timestamp)),
        datasets: [{
          label: 'PM10',
          data: airQualityData.map(d => parseFloat(d.pm10)),
          borderColor: 'rgb(153, 102, 255)',
          tension: 0.1
        }]
      };

      setChartData({ pm25: pm25Data, pm10: pm10Data });
    }
  }, [airQualityData]);

  useEffect(() => {
    const fetchPreferences = async () => {
      try {
        const { data, error } = await supabase
          .from('user_preferences')
          .select('*')
          .eq('user_id', user.id)
          .single();

        if (error) throw error;

        if (data) {
          setHasHVAC(data.has_HVAC);
          setHasEcologica(data.has_ecologgica);
          setFirstName(data.first_name || '');
          setCity(data.city || 'Toronto');
        }
      } catch (error) {
        console.error('Error fetching preferences:', error);
      }
    };

    const fetchAirQualityData = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/airqualitydata');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        console.log('Received air quality data:', data);
        setAirQualityData(data);
      } catch (error) {
        console.error('Error fetching air quality:', error);
      } finally {
        setIsLoading(false);
      }
    };

    if (user) {
      fetchPreferences();
    }
    fetchAirQualityData();
  }, [user]);

  return (
    <div className="dashboard">
      <h1>{firstName ? `${firstName}'s ` : ''}{city} Dashboard</h1>

      <div className="chart-container">
        <div className="chart" style={{ height: '400px', width: '100%' }}>
          <h2>PM2.5 Levels</h2>
          {chartData && (
            <Line
              key="pm25-chart"
              data={chartData.pm25}
              options={chartOptions}
            />
          )}
        </div>

        <div className="chart" style={{ height: '400px', width: '100%' }}>
          <h2>PM10 Levels</h2>
          {chartData && (
            <Line
              key="pm10-chart"
              data={chartData.pm10}
              options={chartOptions}
            />
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
