import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

ChartJS.defaults.responsive = true;
ChartJS.defaults.maintainAspectRatio = false;

const calculateHVACReduction = (value) => {
  return value * 0.7; // 30% reduction
};

const calculateEcologicaReduction = (value) => {
  return value * 0.6; // 40% reduction
};

const calculateCombinedReduction = (value) => {
  return value * 0.5; // 50% reduction
};

const getLast100Days = (data, cityParam) => {
  const today = new Date();

  // Filter data points
  return data.filter(point => {
    // Convert string date to Date object if needed
    const pointDate = (point.date instanceof Date) ? point.date : new Date(point.date);

    // Check if date is valid and not in the future
    if (isNaN(pointDate.getTime()) || pointDate > today) {
      return false;
    }

    // Check if within last 100 days
    const daysDiff = (today - pointDate) / (1000 * 60 * 60 * 24);
    return daysDiff <= 100 && point.city === cityParam;
  });
};

const calculateDaysOverThreshold = (data, pollutantType, threshold, cityParam) => {
  console.log('calculateDaysOverThreshold inputs:', {
    dataLength: data?.length,
    pollutantType,
    threshold,
    cityParam,
    firstDataPoint: data?.[0]
  });
  const last100Days = getLast100Days(data, cityParam);
  console.log('last100Days length:', last100Days.length);
  const result = last100Days.filter(day => {
    const value = parseFloat(day[pollutantType]);
    console.log('checking value:', value, 'against threshold:', threshold);
    return value > threshold;
  }).length;
  console.log('threshold result:', result);
  return result;
};

const calculateDaysOverPeak = (data, pollutantType, threshold, cityParam) => {
  const last100Days = getLast100Days(data, cityParam);
  return last100Days.filter(day => 
    parseFloat(day[pollutantType]) > threshold
  ).length;
};

const calculateDeeperSleepMinutes = (data, hasHVAC, hasEcologica, cityParam) => {
  const last100Days = getLast100Days(data, cityParam);
  const getAdjustedValue = (value) => {
    if (hasHVAC && hasEcologica) {
      return calculateCombinedReduction(value);
    } else if (hasHVAC) {
      return calculateHVACReduction(value);
    } else if (hasEcologica) {
      return calculateEcologicaReduction(value);
    }
    return value;
  };

  const daysUnderThreshold = last100Days.filter(day => {
    const adjustedValue = getAdjustedValue(parseFloat(day['PM 2.5']));
    return adjustedValue <= 5;
  }).length;

  const deeperSleepMinutes = daysUnderThreshold * 8 * 60;
  return deeperSleepMinutes;
};

const Dashboard = () => {
  const [airQualityData, setAirQualityData] = useState([]);
  const [chartData, setChartData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [hasHVAC, setHasHVAC] = useState(false);
