      datasets.push({
        label: `${pollutantType} with Ecologica`,
        data: data.map(d => ({
          x: d.date,
          y: calculateEcologicaReduction(pollutantType === 'PM10' ? parseFloat(d['PM 10']) : parseFloat(d['PM 2.5']))
        })),
        borderColor: 'rgb(60, 179, 113)', // Medium sea green
        backgroundColor: 'rgba(60, 179, 113, 0.1)',
        borderWidth: 2,
        tension: 0.1,
        hidden: !activeDatasets[`${pollutantType} with Ecologica`]
      });
    }

    if (hasHVAC && hasEcologica) {
      datasets.push({
        label: `${pollutantType} with Both`,
        data: data.map(d => ({
          x: d.date,
          y: calculateCombinedReduction(pollutantType === 'PM10' ? parseFloat(d['PM 10']) : parseFloat(d['PM 2.5']))
        })),
        borderColor: 'rgb(144, 238, 144)', // Light green
        backgroundColor: 'rgba(144, 238, 144, 0.1)',
        borderWidth: 2,
        tension: 0.1,
        hidden: !activeDatasets[`${pollutantType} with Both`]
      });
    }

    return { datasets };
  };

  useEffect(() => {
    if (airQualityData.length > 0) {
      console.log('Processing chart data with:', airQualityData.length, 'records');
      setChartData({
        pm25: processChartData(airQualityData, 'PM2.5'),
        pm10: processChartData(airQualityData, 'PM10')
      });
    }
  }, [airQualityData, hasHVAC, hasEcologica, activeDatasets]);

  const getAllValidData = (data) => {
    if (!Array.isArray(data) || data.length === 0) return [];

    return data
      .map(item => ({
        date: new Date(item.Date),
        'PM 2.5': parseFloat(item['PM 2.5']),
        'PM 10': parseFloat(item['PM 10']),
        city: item.City,
        temp: item.Temp
      }))
      .filter(item => 
        !isNaN(item.date.getTime()) && 
        !isNaN(item['PM 10']) && 
        !isNaN(item['PM 2.5']) &&
        item.city === city
      )
      .sort((a, b) => a.date - b.date);
  };

  useEffect(() => {
    async function fetchPreferences() {
      try {
        const { data: { user } } = await supabase.auth.getUser();
        if (!user) return;

        const { data, error } = await supabase
          .from('user_preferences')
          .select('has_HVAC, has_ecologgica, city')
          .eq('user_id', user.id)
          .single();

        console.log('Fetched preferences:', data);

        if (error) {
          console.error('Error fetching preferences:', error);
          return;
        }

        if (data) {
          console.log('Setting city to:', data.city);
          setCity(data.city);
          setHasHVAC(data.has_HVAC);
          setHasEcologica(data.has_ecologgica);

          setActiveDatasets(prev => ({
            ...prev,
            'PM2.5 with HVAC': data.has_HVAC,
            'PM2.5 with Ecologica': data.has_ecologgica,
            'PM2.5 with Both': (data.has_HVAC && data.has_ecologgica),
            'PM10 with HVAC': data.has_HVAC,
            'PM10 with Ecologica': data.has_ecologgica,
            'PM10 with Both': (data.has_HVAC && data.has_ecologgica)
          }));
        }
      } catch (error) {
        console.error('Error:', error);
      }
    }

    fetchPreferences();
  }, []);

  const fetchAirQualityData = async () => {
    try {
      console.log('Fetching data for city:', city);
      const response = await fetch(`http://localhost:5000/api/airqualitydata?city=${city}`);
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      const data = await response.json();
      console.log('Raw data received:', data.length, 'records');

      const last100DaysData = getLast100Days(data, city);
      console.log('Filtered 100 days data:', last100DaysData.length, 'records');

      setAirQualityData(last100DaysData);
    } catch (error) {
      console.error('Error fetching air quality:', error);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    console.log('Triggering fetch effect with city:', city);
    fetchAirQualityData();
  }, [city]);

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: true,
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day'
        },
        grid: {
          display: true,
          color: 'rgba(0, 0, 0, 0.1)',
        }
      },
      y: {
        beginAtZero: true,
        grid: {
          display: true,
          color: 'rgba(0, 0, 0, 0.1)',
        },
        ticks: {
          callback: function(value) {
            return value + ' ug/m³';
          }
        }
      }
    },
    plugins: {
      tooltip: {
        mode: 'index',
        intersect: false,
        callbacks: {
          label: function(context) {
            return `${context.dataset.label}: ${context.parsed.y.toFixed(1)} ug/m³`;
          }
        }
      },
      legend: {
        position: 'bottom',
        labels: {
          usePointStyle: true,
          padding: 15,
          onClick: (event, legendItem, legend) => {
            const index = legendItem.datasetIndex;
            const ci = legend.chart;
            const meta = ci.getDatasetMeta(index);

            meta.hidden = !meta.hidden;

            setActiveDatasets(prev => ({
              ...prev,
              [legendItem.text]: !meta.hidden
            }));

            ci.update();
          }
        }
      }
    }
  };

  return (
    <div className="dashboard">
      {airQualityData.length > 0 && (
        <div className="deeper-sleep-banner" style={{
          backgroundColor: '#90c789',
          color: '#1a472a',
          padding: '20px',
          borderRadius: '8px',
          marginBottom: '20px',
          textAlign: 'center',
          fontSize: '1.5rem',
          fontWeight: 'bold'
        }}>
          {calculateDeeperSleepMinutes(airQualityData, hasHVAC, hasEcologica, city).toLocaleString()} minutes of deeper sleep
        </div>
      )}
      <h1>{firstName ? `${firstName}'s ` : ''}{city} Dashboard</h1>

      <div className="dashboard-container">
        {/* PM2.5 Section */}
        <div className="dashboard-section">
          <div className="content-wrapper">
            <div className="chart-side">
              <h2>PM2.5 Levels</h2>
              {chartData && chartData.pm25 && (
                <Line
                  data={chartData.pm25}
                  options={chartOptions}
                />
              )}
            </div>

            <div className="data-side">
              <div className="key-data-title">
                KEY DATA POINTS
              </div>
              <div className="key-data-points">
                <div className="key-data-point">
                  <span className="key-data-number">
                    {airQualityData && airQualityData.length > 0 ? 
                      calculateDaysOverThreshold(airQualityData, 'PM 2.5', 10, city) : '-'}
                  </span>
                  <span className="key-data-label">
                    days over<br />
                    {10}ug/m³
                  </span>
                </div>
                <div className="key-data-point">
                  <span className="key-data-number">
                    {airQualityData && airQualityData.length > 0 ? 
                      calculateDaysOverThreshold(airQualityData, 'PM 2.5', 20, city) : '-'}
                  </span>
                  <span className="key-data-label">
                    days over<br />
                    {20}ug/m³
                  </span>
                </div>
                <div className="key-data-point">
                  <span className="key-data-number">
                    {airQualityData && airQualityData.length > 0 ? 
                      calculateDaysOverPeak(airQualityData, 'PM 2.5', 50, city) : '-'}
                  </span>
                  <span className="key-data-label">
                    days over<br />
                    {50}ug/m³
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* PM10 Section */}
        <div className="dashboard-section">
          <div className="content-wrapper">
            <div className="chart-side">
              <h2>PM10 Levels</h2>
              {chartData && chartData.pm10 && (
                <Line
                  data={chartData.pm10}
                  options={chartOptions}
                />
              )}
            </div>

            <div className="data-side">
              <div className="key-data-title">
                KEY DATA POINTS
              </div>
              <div className="key-data-points">
                <div className="key-data-point">
                  <span className="key-data-number">
                    {airQualityData && airQualityData.length > 0 ? 
                      calculateDaysOverThreshold(airQualityData, 'PM 10', 20, city) : '-'}
                  </span>
                  <span className="key-data-label">
                    days over<br />
                    {20}ug/m³
                  </span>
                </div>
                <div className="key-data-point">
                  <span className="key-data-number">
                    {airQualityData && airQualityData.length > 0 ? 
                      calculateDaysOverThreshold(airQualityData, 'PM 10', 40, city) : '-'}
                  </span>
                  <span className="key-data-label">
                    days over<br />
                    {40}ug/m³
                  </span>
                </div>
                <div className="key-data-point">
                  <span className="key-data-number">
                    {airQualityData && airQualityData.length > 0 ? 
                      calculateDaysOverPeak(airQualityData, 'PM 10', 50, city) : '-'}
                  </span>
                  <span className="key-data-label">
                    days over<br />
                    {50}ug/m³
                  </span>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
