import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import { supabase } from './supabaseClient';
import Login from './Login';
import Signup from './Signup';
import Dashboard from './Dashboard';  // Your main dashboard or homepage after login

const App = () => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUser = async () => {
      const { data: { user }, error } = await supabase.auth.getUser();
      if (error) {
        console.error("Error fetching user:", error.message);
        setUser(null);
      } else {
        setUser(user);  // Set the authenticated user
      }
      setLoading(false);  // Set loading to false when user state is set
    };

    fetchUser();

    // Listen for changes in the authentication state
    const { data: authListener } = supabase.auth.onAuthStateChange((event, session) => {
      if (session) {
        setUser(session.user);  // Update user if session is valid
      } else {
        setUser(null);  // Set user to null if no session
      }
    });

    // Cleanup listener on unmount
    return () => {
      authListener.unsubscribe();
    };
  }, []);

  if (loading) return <div>Loading...</div>;  // Optionally, show a loading spinner

  return (
    <Router>
      <Routes>
        {/* Redirect to Dashboard if user is logged in */}
        <Route path="/" element={user ? <Dashboard /> : <Navigate to="/login" />} />
        <Route path="/login" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        {/* Redirect to login page if the route doesn't exist */}
        <Route path="*" element={<Navigate to="/login" />} />
      </Routes>
    </Router>
  );
};

export default App;
