import React, { useState, useEffect } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
} from 'chart.js';
import { Bar } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';
import { supabase } from '../../supabaseClient';
import ChartLegend from './ChartLegend';
import { calculateIndoorWithDevices } from '../../utils/airQualityCalculations';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

const PM25Chart = ({ data, userPreferences }) => {
  const [chartData, setChartData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);
  const [activeDatasets, setActiveDatasets] = useState({
    'Outdoor': true,
    'Indoor': true
  });
  const [showEcologica, setShowEcologica] = useState(userPreferences?.hasEcologica || false);

  const toggleDataset = (name) => {
    setActiveDatasets(prev => ({
      ...prev,
      [name]: !prev[name]
    }));
  };

  useEffect(() => {
    try {
      if (!data || data.length === 0) {
        setError('No data available');
        return;
      }

      const formattedData = {
        labels: data.map(item => new Date(item.date)),
        datasets: []
      };

      // Add With Air Purifier (bottom layer)
      if (showEcologica) {
        formattedData.datasets.push({
          label: 'With Air Purifier',
          data: data.map(item => calculateIndoorWithDevices(
            item['PM 2.5'], 
            userPreferences?.has_HVAC || false,
            true
          )),
          backgroundColor: 'rgba(169, 237, 138, 0.9)',
          hidden: false,
          stack: 'stack1',
          barThickness: 'flex'
        });
      }

      // Add Indoor (middle layer)
      formattedData.datasets.push({
        label: 'Indoor',
        data: data.map(item => calculateIndoorWithDevices(
          item['PM 2.5'],
          userPreferences?.has_HVAC || false,
          false
        )),
        backgroundColor: 'rgba(217, 246, 187, 0.9)',
        hidden: !activeDatasets['Indoor'],
        stack: 'stack1',
        barThickness: 'flex'
      });

      // Add Outdoor (top layer)
      formattedData.datasets.push({
        label: 'Outdoor',
        data: data.map(item => item['PM 2.5']),
        backgroundColor: 'rgba(4, 58, 36, 0.9)',
        hidden: !activeDatasets['Outdoor'],
        stack: 'stack1',
        barThickness: 'flex'
      });

      setChartData(formattedData);
      setIsLoading(false);
    } catch (err) {
      console.error('Error processing PM2.5 data:', err);
      setError('Failed to process PM2.5 data');
      setIsLoading(false);
    }
  }, [data, showEcologica, activeDatasets, userPreferences]);

  if (isLoading) return <div>Loading PM2.5 data...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!chartData || !data.length) return <div>No PM2.5 data available</div>;

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        stacked: true,
        type: 'time',
        time: {
          unit: 'day',
          displayFormats: {
            day: 'MMM d'
          }
        },
        title: {
          display: true,
          text: 'Date'
        },
        grid: {
          display: false
        }
      },
      y: {
        stacked: true,
        beginAtZero: true,
        title: {
          display: true,
          text: 'μg/m³'
        },
        grid: {
          color: 'rgba(0, 0, 0, 0.1)'
        }
      }
    },
    plugins: {
      legend: {
        display: false
      },
      tooltip: {
        mode: 'index',
        intersect: false,
        callbacks: {
          label: function(context) {
            const label = context.dataset.label || '';
            const value = context.parsed.y;
            return `${label}: ${value.toFixed(1)} μg/m³`;
          }
        }
      }
    }
  };

  return (
    <div style={{ width: '100%', height: '100%', position: 'relative' }}>
      <Bar data={chartData} options={options} />
      <ChartLegend 
        activeDatasets={{
          'Outdoor': activeDatasets['Outdoor'],
          'Indoor': activeDatasets['Indoor'],
          'With Air Purifier': showEcologica
        }}
        onToggle={(label) => {
          if (label === 'With Air Purifier') {
            setShowEcologica(!showEcologica);
          } else {
            toggleDataset(label);
          }
        }}
        showAirPurifier={userPreferences?.hasEcologica}
      />
    </div>
  );
};

export default PM25Chart;
