import React, { useEffect, useState, useRef } from 'react';
import { supabase } from './supabaseClient';
import Chart from 'chart.js/auto';
import 'chartjs-adapter-moment';
import dayjs from 'dayjs';
import { Line } from 'react-chartjs-2';

const Dashboard = ({ user }) => {
  const [city, setCity] = useState('');
  const [hasHVAC, setHasHVAC] = useState(false);
  const [hasEcologica, setHasEcologica] = useState(false);
  const [airQualityData, setAirQualityData] = useState([]);
  const [keyDataPoints, setKeyDataPoints] = useState({ over10: 0, over20: 0, over50: 0 });
  const [error, setError] = useState(null);
  const pm25ChartInstance = useRef(null);
  const pm10ChartInstance = useRef(null);
  const pm25Ref = useRef(null);
  const pm10Ref = useRef(null);
  const [firstName, setFirstName] = useState('');

  useEffect(() => {
    if (user) {
      fetchPreferences();
    } else {
      console.error("User is not defined, cannot fetch preferences.");
    }
  }, [user]);

  const fetchPreferences = async () => {
    if (!user || !user.id) {
      console.error("User ID is undefined, cannot fetch preferences.");
      setError("User not authenticated.");
      return;
    }

    console.log("Fetching preferences for User ID:", user.id);

    const { data, error } = await supabase
      .from('user_preferences')
      .select('*')
      .eq('user_id', user.id)
      .single();

    if (error) {
      console.error("Error fetching preferences:", error.message);
      setError("Error fetching preferences: " + error.message);
      return;
    }

    if (data) {
      console.log("Fetched user preferences:", data);
      setCity(data.city);
      setHasHVAC(data.has_HVAC);
      setHasEcologica(data.has_ecologgica);
      setFirstName(data.first_name || '');
      fetchAirQualityData(data.city);
    }
  };

  const fetchAirQualityData = async (selectedCity) => {
    console.log("Selected City:", selectedCity);
    try {
      const response = await fetch('http://localhost:5000/api/airqualitydata');
      const data = await response.json();
      console.log("Raw API response:", data);

      // Filter for the selected city
      const cityData = data.filter(row => row.City?.toLowerCase() === selectedCity.toLowerCase());
      console.log("Filtered city data:", cityData);

      if (!cityData.length) {
        console.error(`No data found for city: ${selectedCity}`);
        setError(`No air quality data available for ${selectedCity}`);
        return;
      }

      // Sort data by date
      const sortedData = cityData.sort((a, b) => {
        return dayjs(a.Date, 'M/D/YYYY H:mm:ss').valueOf() - dayjs(b.Date, 'M/D/YYYY H:mm:ss').valueOf();
      });

      // Take the most recent 30 days of data
      const recentData = sortedData.slice(-30);
      console.log("Most recent 30 days of data:", recentData);

      // Apply user preferences to adjust data
      const adjustedData = applyPreferences(recentData);
      setAirQualityData(adjustedData);

      // Initialize charts with adjusted data
      initChart(pm25Ref, pm25ChartInstance, 'PM 2.5', adjustedData.map(row => ({
        x: dayjs(row.Date, 'M/D/YYYY H:mm:ss').toDate(),
        y: row.adjustedPM25
      })));

      initChart(pm10Ref, pm10ChartInstance, 'PM 10', adjustedData.map(row => ({
        x: dayjs(row.Date, 'M/D/YYYY H:mm:ss').toDate(),
        y: row.adjustedPM10
      })));

      // Calculate key data points after charts are initialized
      calculateKeyDataPoints(adjustedData);
    } catch (error) {
      console.error('Error fetching air quality data:', error);
      setError("Error fetching air quality data.");
    }
  };

  const applyPreferences = (data) => {
    return data.map((row) => {
      const adjustedPM25 = row['PM 2.5'] * (hasHVAC ? 0.6 : 1) * (hasEcologica ? 0.75 : 1);
      const adjustedPM10 = row['PM 10'] * (hasEcologica ? 0.75 : 1);
      return { ...row, adjustedPM25, adjustedPM10 };
    });
  };

  const calculateKeyDataPoints = (data) => {
    const over10 = data.filter(row => row.adjustedPM25 > 10).length;
    const over20 = data.filter(row => row.adjustedPM25 > 20).length;
    const over50 = data.filter(row => row.adjustedPM25 > 50).length;
    setKeyDataPoints({ over10, over20, over50 });
  };

  const initChart = (ref, chartInstanceRef, label, data) => {
    console.log(`Initializing ${label} chart with data:`, data);
    if (!ref.current) {
      console.error(`Canvas reference for ${label} is null`);
      return;
    }

    if (chartInstanceRef.current) {
      console.log(`Destroying existing ${label} chart instance...`);
      chartInstanceRef.current.destroy();
    }

    const ctx = ref.current.getContext('2d');

    // Create the new chart
    const newChart = new Chart(ctx, {
      type: 'line',
      data: {
        labels: airQualityData.map(row => row.Date),
        datasets: [{
          label: label,
          data: data,
          borderColor: 'rgba(34, 139, 34, 1)',
          backgroundColor: 'rgba(34, 139, 34, 0.2)',
          tension: 0.1,
          fill: true,
        }],
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        scales: {
          x: {
            type: 'time',
            time: { unit: 'day' },
            title: { display: true, text: 'Date' },
          },
          y: {
            beginAtZero: true,
            title: { display: true, text: 'µg/m³' },
          },
        },
      },
    });

    chartInstanceRef.current = newChart; // Save the new chart instance
  };

  const calculateImprovedAirQuality = (originalValue, hasHVAC, hasEcologica) => {
    let improvedValue = originalValue;

    if (hasHVAC) {
      improvedValue *= 0.5; // Reduce by 50% for HVAC
    }

    if (hasEcologica) {
      improvedValue *= 0.7; // Further reduce by 30% for Ecologica
    }

    return improvedValue.toFixed(2);
  };

  const getChartData = () => {
    const baseData = {
      labels: airQualityData.map(d => d.timestamp),
      datasets: [
        {
          label: 'Original PM2.5',
          data: airQualityData.map(d => d.pm25),
          borderColor: 'rgb(75, 192, 192)',
          tension: 0.1
        }
      ]
    };

    if (hasHVAC || hasEcologica) {
      baseData.datasets.push({
        label: `Improved PM2.5 ${hasHVAC ? '(HVAC)' : ''} ${hasEcologica ? '(Ecologica)' : ''}`,
        data: airQualityData.map(d => 
          calculateImprovedAirQuality(d.pm25, hasHVAC, hasEcologica)
        ),
        borderColor: hasEcologica ? 'rgb(54, 162, 235)' : 'rgb(255, 159, 64)',
        tension: 0.1
      });
    }

    return baseData;
  };

  const getChartData10 = () => {
    const baseData = {
      labels: airQualityData.map(d => d.timestamp),
      datasets: [
        {
          label: 'Original PM10',
          data: airQualityData.map(d => d.pm10),
          borderColor: 'rgb(153, 102, 255)',
          tension: 0.1
        }
      ]
    };

    if (hasHVAC || hasEcologica) {
      baseData.datasets.push({
        label: `Improved PM10 ${hasHVAC ? '(HVAC)' : ''} ${hasEcologica ? '(Ecologica)' : ''}`,
        data: airQualityData.map(d => 
          calculateImprovedAirQuality(d.pm10, hasHVAC, hasEcologica)
        ),
        borderColor: hasEcologica ? 'rgb(54, 162, 235)' : 'rgb(255, 159, 64)',
        tension: 0.1
      });
    }

    return baseData;
  };

  return (
    <div className="dashboard">
      <h1>{firstName ? `${firstName}'s ` : ''}{city} Dashboard</h1>

      <div className="chart-container">
        <div className="chart">
          <h2>PM2.5 Levels</h2>
          {airQualityData.length > 0 ? (
            <Line data={getChartData()} options={chartOptions} />
          ) : (
            <p>Loading data...</p>
          )}
        </div>

        <div className="chart">
          <h2>PM10 Levels</h2>
          {airQualityData.length > 0 ? (
            <Line data={getChartData10()} options={chartOptions} />
          ) : (
            <p>Loading data...</p>
          )}
        </div>
      </div>

      {/* Add a legend explaining the improvements */}
      {(hasHVAC || hasEcologica) && (
        <div className="improvements-legend">
          <h3>Air Quality Improvements:</h3>
          {hasHVAC && <p>• HVAC System: 50% reduction in particulate matter</p>}
          {hasEcologica && <p>• Ecologica System: Additional 30% reduction</p>}
          {hasHVAC && hasEcologica && (
            <p>• Total improvement: 65% reduction in particulate matter</p>
          )}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
