import React, { useState, useEffect } from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';
import { supabase } from '../../supabaseClient';
import ChartLegend from './ChartLegend';
import { calculateIndoorWithDevices } from '../../utils/airQualityCalculations';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

const PM25Chart = ({ data, userPreferences }) => {
  const [chartData, setChartData] = useState(null);
  const [activeDatasets, setActiveDatasets] = useState({
    'Outdoor': true,
    'Indoor': true,
    'HVAC': false,
    'Air Purifier': false
  });

  const toggleDataset = (name) => {
    setActiveDatasets(prev => ({
      ...prev,
      [name]: !prev[name]
    }));
  };

  useEffect(() => {
    if (!data || data.length === 0) return;

    const formattedData = {
      labels: data.map(item => new Date(item.date)),
      datasets: []
    };

    // Add indoor dataset
    formattedData.datasets.push({
      label: 'Indoor',
      data: data.map(item => ({
        x: new Date(item.date),
        y: calculateIndoorWithDevices(item['PM 2.5'], false, false)
      })),
      borderColor: '#D9F6BB',
      backgroundColor: 'rgba(217, 246, 187, 0.1)',
      tension: 0.4,
      hidden: !activeDatasets['Indoor']
    });

    // Add HVAC dataset if enabled
    if (userPreferences?.has_HVAC) {
      formattedData.datasets.push({
        label: 'HVAC',
        data: data.map(item => ({
          x: new Date(item.date),
          y: calculateIndoorWithDevices(item['PM 2.5'], true, false)
        })),
        borderColor: '#A9ED8A',
        backgroundColor: 'rgba(169, 237, 138, 0.1)',
        tension: 0.4,
        hidden: !activeDatasets['HVAC']
      });
    }

    // Add Air Purifier dataset if enabled
    if (userPreferences?.has_ecologgica) {
      formattedData.datasets.push({
        label: 'Air Purifier',
        data: data.map(item => ({
          x: new Date(item.date),
          y: calculateIndoorWithDevices(item['PM 2.5'], false, true)
        })),
        borderColor: '#7FD663',
        backgroundColor: 'rgba(127, 214, 99, 0.1)',
        tension: 0.4,
        hidden: !activeDatasets['Air Purifier']
      });
    }

    // Add outdoor dataset (always last to be on top)
    formattedData.datasets.push({
      label: 'Outdoor',
      data: data.map(item => ({
        x: new Date(item.date),
        y: item['PM 2.5']
      })),
      borderColor: '#043A24',
      backgroundColor: 'rgba(4, 58, 36, 0.1)',
      tension: 0.4,
      hidden: !activeDatasets['Outdoor']
    });

    setChartData(formattedData);
  }, [data, activeDatasets, userPreferences]);

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day',
          displayFormats: {
            day: 'MMM d'
          }
        },
        title: {
          display: true,
          text: 'Date'
        },
        ticks: {
          maxRotation: 0,
          autoSkip: true,
          maxTicksLimit: 10
        }
      },
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'PM2.5 Level (μg/m³)'
        }
      }
    },
    plugins: {
      legend: {
        display: false
      },
      tooltip: {
        mode: 'index',
        intersect: false,
        position: 'nearest'
      }
    },
    layout: {
      padding: {
        left: 10,
        right: 10
      }
    }
  };

  if (!chartData) return null;

  return (
    <div className="chart-side">
      <div className="chart-wrapper">
        <Line data={chartData} options={options} />
      </div>
      <ChartLegend 
        activeDatasets={activeDatasets}
        onToggle={toggleDataset}
        userPreferences={userPreferences}
      />
    </div>
  );
};

export default PM25Chart;
