import React, { useState, useEffect } from 'react';
import { supabase } from '../../supabaseClient';
import PM25Chart from '../../components/charts/PM25Chart';
import PM10Chart from '../../components/charts/PM10Chart';
import CityComparisonChart from '../../components/charts/CityComparisonChart';
import AnxietyRiskChart from '../../components/charts/AnxietyRiskChart';
import HealthImpactPanel from '../../components/HealthImpactPanel';
import '../../styles/style.css';

// Cache management functions
const CACHE_DURATION = 30 * 60 * 1000; // 30 minutes in milliseconds

const getCachedData = (city) => {
  try {
    const cached = localStorage.getItem(`airquality_${city}`);
    if (cached) {
      const { data, timestamp } = JSON.parse(cached);
      // Check if cache is still valid (within 30 minutes)
      if (Date.now() - timestamp < CACHE_DURATION) {
        return data;
      }
    }
    return null;
  } catch (error) {
    console.error('Error reading from cache:', error);
    return null;
  }
};

const setCachedData = (city, data) => {
  try {
    localStorage.setItem(`airquality_${city}`, JSON.stringify({
      data,
      timestamp: Date.now()
    }));
  } catch (error) {
    console.error('Error writing to cache:', error);
  }
};

// Function to fetch data from OpenAQ API
const fetchOpenAQData = async (city, coordinates) => {
  try {
    // Calculate date range for 60 days
    const endDate = new Date();
    const startDate = new Date();
    startDate.setDate(startDate.getDate() - 60);

    // Use CORS proxy for development
    const proxyUrl = 'https://api.allorigins.win/raw?url=';
    const openAQUrl = `https://api.openaq.org/v3/measurements?coordinates=${coordinates.lat},${coordinates.lon}&radius=10000&date_from=${startDate.toISOString()}&date_to=${endDate.toISOString()}&parameter=pm25,pm10&limit=1000`;

    // Get historical air quality data
    const response = await fetch(proxyUrl + encodeURIComponent(openAQUrl), {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
      }
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();

    if (!data.results || data.results.length === 0) {
      console.log('No data available for location:', city);
      return [];
    }

    // Group measurements by day
    const measurementsByDay = data.results.reduce((acc, measurement) => {
      const date = new Date(measurement.date.utc).toISOString().split('T')[0];
      if (!acc[date]) {
        acc[date] = {
          'PM 2.5': null,
          'PM 10': null,
          measurements: []
        };
      }
      acc[date].measurements.push(measurement);

      // Update PM values based on parameter
      if (measurement.parameter === 'pm25') {
        acc[date]['PM 2.5'] = measurement.value;
      } else if (measurement.parameter === 'pm10') {
        acc[date]['PM 10'] = measurement.value;
      }

      return acc;
    }, {});

    // Convert to array and format for our app
    return Object.entries(measurementsByDay).map(([date, data]) => ({
      date: new Date(date).toISOString(),
      'PM 2.5': data['PM 2.5'],
      'PM 10': data['PM 10'],
      city: city,
      health_impact: calculateHealthImpact({ parameters: [
        { parameter: 'pm25', value: data['PM 2.5'] },
        { parameter: 'pm10', value: data['PM 10'] }
      ]}),
      who_guidelines: getWHOGuidelines({ parameters: [
        { parameter: 'pm25', value: data['PM 2.5'] },
        { parameter: 'pm10', value: data['PM 10'] }
      ]}),
      source: 'openaq'
    }));
  } catch (error) {
    console.error('Error fetching OpenAQ data:', error);
    return [];
  }
};

// Helper function to calculate health impact based on PM2.5 and PM10 levels
const calculateHealthImpact = (measurement) => {
  const pm25 = measurement.parameters.find(p => p.parameter === 'pm25')?.value;
  const pm10 = measurement.parameters.find(p => p.parameter === 'pm10')?.value;

  if (!pm25 && !pm10) return 'No data available';

  // Using WHO guidelines for health impact assessment
  if (pm25 <= 10 && pm10 <= 20) return 'Good';
  if (pm25 <= 25 && pm10 <= 50) return 'Moderate';
  if (pm25 <= 50 && pm10 <= 100) return 'Unhealthy for Sensitive Groups';
  if (pm25 <= 75 && pm10 <= 200) return 'Unhealthy';
  return 'Very Unhealthy';
};

// Helper function to get WHO guidelines status
const getWHOGuidelines = (measurement) => {
  const pm25 = measurement.parameters.find(p => p.parameter === 'pm25')?.value;
  const pm10 = measurement.parameters.find(p => p.parameter === 'pm10')?.value;

  return {
    'PM2.5': {
      value: pm25,
      threshold: 10,
      exceeds: pm25 > 10
    },
    'PM10': {
      value: pm10,
      threshold: 20,
      exceeds: pm10 > 20
    }
  };
};

// Utility function for deeper sleep calculation
const calculateDeeperSleepMinutes = (data, hasEcologica) => {
  const getAdjustedValue = (value) => {
    // Always apply indoor reduction first
    const indoorValue = value * 0.7;
    // Then apply Ecologica if present
    if (hasEcologica) return value * 0.5;
    return indoorValue;
  };

  const daysUnderThreshold = data.filter(day => {
    const adjustedValue = getAdjustedValue(parseFloat(day['PM 2.5']));
    return adjustedValue <= 5;
  }).length;

  return daysUnderThreshold * 8 * 60;
};

const Dashboard = () => {
  const [userPreferences, setUserPreferences] = useState({
    hasEcologica: false,
    city: 'Toronto',
    firstName: '',
    anxietyLevel: 5
  });
  const [airQualityData, setAirQualityData] = useState([]);
  const [healthData, setHealthData] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchUserPreferences = async () => {
      try {
        const { data: { user } } = await supabase.auth.getUser();
        if (!user) return;

        const { data, error } = await supabase
          .from('user_preferences')
          .select('*')
          .eq('user_id', user.id)
          .single();

        if (error) throw error;

        if (data) {
          setUserPreferences({
            hasEcologica: data.has_ecologgica || false,
            city: data.city || 'Toronto',
            firstName: data.first_name || '',
            anxietyLevel: data.anxiety_base_level || 5
          });
        }
      } catch (error) {
        console.error('Error fetching preferences:', error);
      }
    };

    fetchUserPreferences();
  }, []);

  const fetchAirQualityData = async () => {
    try {
      setIsLoading(true);

      const cityCoordinates = {
        'Toronto': { lat: 43.6532, lon: -79.3832 },
        'Vancouver': { lat: 49.2827, lon: -123.1207 },
        'Montreal': { lat: 45.5017, lon: -73.5673 },
        'Boston': { lat: 42.3601, lon: -71.0589 },
        'Calgary': { lat: 51.0447, lon: -114.0719 },
        'Dallas': { lat: 32.7767, lon: -96.7970 },
        'Edmonton': { lat: 53.5461, lon: -113.4938 },
        'Miami': { lat: 25.7617, lon: -80.1918 },
        'New York': { lat: 40.7128, lon: -74.0060 },
        'San Francisco': { lat: 37.7749, lon: -122.4194 }
      }[userPreferences.city] || { lat: 43.6532, lon: -79.3832 };

      // Check cache first
      const cachedData = getCachedData(userPreferences.city);
      if (cachedData) {
        console.log('Using cached data for:', userPreferences.city);
        setAirQualityData(cachedData);
        if (cachedData.length > 0) {
          setHealthData({
            pm25: cachedData[0]['PM 2.5'],
            health_impact: cachedData[0].health_impact,
            who_guidelines: cachedData[0].who_guidelines
          });
        }
        setIsLoading(false);
        return;
      }

      // Fetch from OpenAQ if no cache or cache expired
      const proxyUrl = 'https://cors-anywhere.herokuapp.com/';
      const openAQUrl = `https://api.openaq.org/v3/measurements?coordinates=${cityCoordinates.lat},${cityCoordinates.lon}&radius=15000&parameters=pm25,pm10&limit=1000&date_from=${new Date(Date.now() - 60 * 24 * 60 * 60 * 1000).toISOString()}&date_to=${new Date().toISOString()}`;

      const response = await fetch(proxyUrl + openAQUrl, {
        method: 'GET',
        headers: {
          'Content-Type': 'application/json',
          'Origin': window.location.origin
        }
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();

      if (!data.results || data.results.length === 0) {
        console.log('No data available for:', userPreferences.city);
        setAirQualityData([]);
        setIsLoading(false);
        return;
      }

      // Process and group the data by day
      const measurementsByDay = data.results.reduce((acc, measurement) => {
        const date = new Date(measurement.date.utc).toISOString().split('T')[0];
        if (!acc[date]) {
          acc[date] = {
            'PM 2.5': null,
            'PM 10': null,
            measurements: []
          };
        }
        acc[date].measurements.push(measurement);

        if (measurement.parameter === 'pm25') {
          acc[date]['PM 2.5'] = measurement.value;
        } else if (measurement.parameter === 'pm10') {
          acc[date]['PM 10'] = measurement.value;
        }

        return acc;
      }, {});

      // Convert to array format
      const processedData = Object.entries(measurementsByDay)
        .map(([date, data]) => ({
          date: new Date(date).toISOString(),
          'PM 2.5': data['PM 2.5'],
          'PM 10': data['PM 10'],
          city: userPreferences.city,
          health_impact: calculateHealthImpact({
            parameters: [
              { parameter: 'pm25', value: data['PM 2.5'] },
              { parameter: 'pm10', value: data['PM 10'] }
            ]
          }),
          who_guidelines: getWHOGuidelines({
            parameters: [
              { parameter: 'pm25', value: data['PM 2.5'] },
              { parameter: 'pm10', value: data['PM 10'] }
            ]
          }),
          source: 'openaq'
        }))
        .sort((a, b) => new Date(b.date) - new Date(a.date));

      // Cache the processed data
      setCachedData(userPreferences.city, processedData);

      // Update state
      setAirQualityData(processedData);
      if (processedData.length > 0) {
        setHealthData({
          pm25: processedData[0]['PM 2.5'],
          health_impact: processedData[0].health_impact,
          who_guidelines: processedData[0].who_guidelines
        });
      }
    } catch (error) {
      console.error('Error fetching air quality data:', error);
      setAirQualityData([]);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    if (userPreferences.city) {
      fetchAirQualityData();
    }

    const interval = setInterval(() => {
      if (userPreferences.city) {
        fetchAirQualityData();
      }
    }, CACHE_DURATION); // Refresh when cache expires

    return () => clearInterval(interval);
  }, [userPreferences.city]);

  if (isLoading) return <div>Loading...</div>;

  return (
    <div className="dashboard">
      <h1>
        {userPreferences.firstName ? `${userPreferences.firstName}'s ` : ''}
        Air Quality Dashboard
      </h1>

      <div className="dashboard-section">
        <HealthImpactPanel healthData={healthData} />
      </div>

      <div className="dashboard-section">
        <div className="chart-container">
          <div className="chart-area">
            <h2>PM2.5 Levels</h2>
            <PM25Chart data={airQualityData} />
          </div>
        </div>
      </div>

      <div className="dashboard-section">
        <h2>PM10 Levels</h2>
        <PM10Chart data={airQualityData} />
      </div>

      <div className="dashboard-section">
        <h2>City Comparison</h2>
        <CityComparisonChart data={airQualityData} />
      </div>

      <div className="dashboard-section">
        <h2>Anxiety Risk</h2>
        <AnxietyRiskChart data={airQualityData} userPreferences={userPreferences} />
      </div>
    </div>
  );
};

export default Dashboard;
