// Dashboard.js
import React, { useEffect, useState } from 'react';
import { supabase } from './supabaseClient';
import Chart from 'chart.js/auto';
import Navbar from './Navbar';

const Dashboard = () => {
  const [city, setCity] = useState('');
  const [hasHVAC, setHasHVAC] = useState(false);
  const [hasEcologica, setHasEcologica] = useState(false);
  const [airQualityData, setAirQualityData] = useState([]);
  const [keyDataPoints, setKeyDataPoints] = useState({
    over10: 0,
    over20: 0,
    over50: 0,
  });

  // Fetch user preferences
  useEffect(() => {
    const fetchPreferences = async () => {
      const { data, error } = await supabase
        .from('user_preferences')
        .select('*')
        .single();

      if (data) {
        setCity(data.city);
        setHasHVAC(data.has_HVAC);
        setHasEcologica(data.has_ecologica);
      } else if (error) {
        console.error('Error fetching preferences:', error);
      }
    };

    fetchPreferences();
  }, []);

  // Fetch air quality data and calculate key data points
  useEffect(() => {
    if (!city) return;

    const fetchAirQualityData = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/airqualitydata');
        const data = await response.json();
        setAirQualityData(data);

        // Filter data by city and apply adjustments
        const cityData = data.filter(row => row.City === city);
        const pm25Multiplier = hasHVAC ? 0.6 : 1;
        const pm10Multiplier = hasEcologica ? 0.75 : 1;

        // Adjust PM2.5 and PM10 data if HVAC/Ecologica are enabled
        const adjustedPm25Data = cityData.map(row => row['PM 2.5'] * pm25Multiplier);
        const adjustedPm10Data = cityData.map(row => row['PM 10'] * pm10Multiplier);

        calculateKeyDataPoints(adjustedPm25Data);
        initChart('pm25Chart', 'PM 2.5', adjustedPm25Data);
        initChart('pm10Chart', 'PM 10', adjustedPm10Data);
      } catch (error) {
        console.error('Error fetching air quality data:', error);
      }
    };

    fetchAirQualityData();
  }, [city, hasHVAC, hasEcologica]);

  // Calculate key data points
  const calculateKeyDataPoints = (pm25Data) => {
    const over10 = pm25Data.filter(value => value > 10).length;
    const over20 = pm25Data.filter(value => value > 20).length;
    const over50 = pm25Data.filter(value => value > 50).length;

    setKeyDataPoints({ over10, over20, over50 });
  };

  const initChart = (elementId, label, data) => {
    const ctx = document.getElementById(elementId).getContext('2d');
    new Chart(ctx, {
      type: 'line',
      data: {
        labels: airQualityData.map(row => row.Date),
        datasets: [{
          label: label,
          data: data,
          borderColor: 'rgba(34, 139, 34, 1)', // Green color
          backgroundColor: 'rgba(34, 139, 34, 0.2)',
          tension: 0.1,
          fill: true,
        }]
      },
      options: {
        scales: {
          x: { type: 'time', time: { unit: 'day', displayFormats: { day: 'MMM DD' }}},
          y: { beginAtZero: true }
        }
      }
    });
  };

  return (
    <div className="dashboard">
      <Navbar />
      <h1>{city} Air Quality</h1>
      <div className="dashboard-content">
        <div className="charts-container">
          <canvas id="pm25Chart"></canvas>
          <canvas id="pm10Chart"></canvas>
        </div>
        <div className="key-data-points">
          <h3>Key Data Points</h3>
          <p><strong>{keyDataPoints.over10}</strong> days over 10 µg/m³</p>
          <p><strong>{keyDataPoints.over20}</strong> days over 20 µg/m³</p>
          <p><strong>{keyDataPoints.over50}</strong> peaked at 50 µg/m³</p>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
