import React, { useState, useEffect } from 'react';
import { supabase } from '../supabaseClient';
import { useNavigate } from 'react-router-dom';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import './styles.css';

const UserPreferences = () => {
  const [availableCities, setAvailableCities] = useState([
    'Toronto',
    'San Francisco',
    'New York',
    'Dallas',
    'Boston',
    'Miami',
    'Calgary',
    'Edmonton'

  ].sort());

  const [preferences, setPreferences] = useState({
    has_HVAC: false,
    has_ecologgica: false,
    first_name: '',
    last_name: '',
    city: 'Toronto',
    anxiety_base_level: 5, // Default middle value
    track_anxiety: false, // New checkbox state
    birthdate: null,
    // New health and household fields
    has_children: false,
    children_details: '',
    has_pets: false,
    pets_details: '',
    allergies: {
      pet_dander: false,
      dust_mites: false,
      pollen: false,
      mold: false,
      none: false
    },
    health_issues: {
      sleep_quality: false,
      respiratory_issues: false,
      asthma: false,
      skin_irritation: false,
      none: false
    }
  });
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPreferences = async () => {
      try {
        const { data: { user } } = await supabase.auth.getUser();
        if (!user) {
          navigate('/login');
          return;
        }

        const { data, error } = await supabase
          .from('user_preferences')
          .select('*')
          .eq('user_id', user.id)
          .single();

        if (error) throw error;
        if (data) {
          setPreferences(data);
        }
      } catch (error) {
        console.error('Error fetching preferences:', error);
      }
    };

    fetchPreferences();
  }, [navigate]);

  useEffect(() => {
    const fetchCities = async () => {
      try {
        const { data, error } = await supabase
          .from('airqualitydata')
          .select('city')
          .order('city');

        if (error) {
          throw error;
        }

        if (data) {
          const uniqueCities = [...new Set(data.map(item => item.city))];
          setAvailableCities(uniqueCities);
        }
      } catch (error) {
        console.error('Error fetching cities:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchCities();
  }, []);

  const handleSave = async (e) => {
    e.preventDefault();

    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        alert('Please log in to save preferences');
        navigate('/login');
        return;
      }

      const { error } = await supabase
        .from('user_preferences')
        .upsert({ 
          user_id: user.id,
          has_ecologgica: preferences.has_ecologgica,
          first_name: preferences.first_name,
          last_name: preferences.last_name,
          city: preferences.city,
          anxiety_base_level: Number(preferences.anxiety_base_level),
          track_anxiety: preferences.track_anxiety,
          birthdate: preferences.birthdate,
          // New fields
          has_children: preferences.has_children,
          children_details: preferences.children_details,
          has_pets: preferences.has_pets,
          pets_details: preferences.pets_details,
          allergies: preferences.allergies,
          health_issues: preferences.health_issues
        }, {
          onConflict: 'user_id',
          ignoreDuplicates: false
        });

      if (error) throw error;

      alert('Preferences saved successfully!');
      navigate('/dashboard');
    } catch (error) {
      console.error('Error saving preferences:', error);
      alert('Error saving preferences: ' + error.message);
    }
  };

  return (
    <div className="preferences-container">
      <div className="preferences-content">
        <h1 className="preferences-title">User Preferences</h1>

        <form onSubmit={handleSave} className="preferences-form">
          <div className="form-group">
            <label>First Name:</label>
            <input
              type="text"
              value={preferences.first_name}
              onChange={(e) => setPreferences({ 
                ...preferences, 
                first_name: e.target.value 
              })}
            />
          </div>

          <div className="form-group">
            <label>Last Name:</label>
            <input
              type="text"
              value={preferences.last_name}
              onChange={(e) => setPreferences({ 
                ...preferences, 
                last_name: e.target.value 
              })}
            />
          </div>

          <div className="form-group">
            <label>Birthdate:</label>
            <DatePicker
              selected={preferences.birthdate ? new Date(preferences.birthdate) : null}
              onChange={(date) => setPreferences({
                ...preferences,
                birthdate: date
              })}
              dateFormat="MMMM d, yyyy"
              placeholderText="Select your birthdate"
              className="date-picker"
              maxDate={new Date()}
              showYearDropdown
              scrollableYearDropdown
              yearDropdownItemNumber={100}
            />
          </div>

          <div className="form-group">
            <label>City:</label>
            <select
              value={preferences.city}
              onChange={(e) => setPreferences({ 
                ...preferences, 
                city: e.target.value 
              })}
            >
              {availableCities.map((city) => (
                <option key={city} value={city}>
                  {city}
                </option>
              ))}
            </select>
          </div>

          <div className="form-group">
            <label>Base Anxiety Level (1-10):</label>
            <input
              type="range"
              min="1"
              max="10"
              value={preferences.anxiety_base_level}
              onChange={(e) => setPreferences({ 
                ...preferences, 
                anxiety_base_level: parseInt(e.target.value) 
              })}
              className="form-slider"
            />
            <span className="slider-value">{preferences.anxiety_base_level}</span>
          </div>

          <div className="form-group">
            <label className="checkbox-label">
              <input
                type="checkbox"
                checked={preferences.track_anxiety}
                onChange={(e) => setPreferences({ 
                  ...preferences, 
                  track_anxiety: e.target.checked 
                })}
              />
              Track Anxiety Levels
            </label>
          </div>

          <div className="form-group">
            <label className="checkbox-label">
              <input
                type="checkbox"
                checked={preferences.has_ecologgica}
                onChange={(e) => setPreferences({ 
                  ...preferences, 
                  has_ecologgica: e.target.checked 
                })}
              />
              Has Ecologgica Product
            </label>
          </div>

          <h2 className="section-title">Health and Household Context</h2>

          <div className="form-group">
            <label>
              <span>üëß Do you have children?</span>
              <input
                type="checkbox"
                checked={preferences.has_children}
                onChange={(e) => setPreferences({
                  ...preferences,
                  has_children: e.target.checked,
                  children_details: !e.target.checked ? '' : preferences.children_details
                })}
              />
            </label>

            {preferences.has_children && (
              <div className="sub-field">
                <label>How many and what ages?</label>
                <input
                  type="text"
                  value={preferences.children_details}
                  onChange={(e) => setPreferences({
                    ...preferences,
                    children_details: e.target.value
                  })}
                  placeholder="e.g., 2 children: 5 and 8 years old"
                />
              </div>
            )}
          </div>

          <div className="form-group">
            <label>
              <span>üê∂ Do you have pets?</span>
              <input
                type="checkbox"
                checked={preferences.has_pets}
                onChange={(e) => setPreferences({
                  ...preferences,
                  has_pets: e.target.checked,
                  pets_details: !e.target.checked ? '' : preferences.pets_details
                })}
              />
            </label>

            {preferences.has_pets && (
              <div className="sub-field">
                <label>What kind?</label>
                <input
                  type="text"
                  value={preferences.pets_details}
                  onChange={(e) => setPreferences({
                    ...preferences,
                    pets_details: e.target.value
                  })}
                  placeholder="e.g., 2 dogs, 1 cat"
                />
              </div>
            )}
          </div>

          <div className="form-group">
            <label>ü§ß Allergies in the household? (Check all that apply)</label>
            <div className="checkbox-group">
              {Object.entries({
                pet_dander: 'Pet dander',
                dust_mites: 'Dust mites',
                pollen: 'Pollen',
                mold: 'Mold',
                none: 'None'
              }).map(([key, label]) => (
                <label key={key} className="checkbox-label">
                  <input
                    type="checkbox"
                    checked={preferences.allergies[key]}
                    onChange={(e) => {
                      if (key === 'none') {
                        // If "None" is selected, uncheck all others
                        setPreferences({
                          ...preferences,
                          allergies: {
                            pet_dander: false,
                            dust_mites: false,
                            pollen: false,
                            mold: false,
                            none: e.target.checked
                          }
                        });
                      } else {
                        // If any other is selected, uncheck "None"
                        setPreferences({
                          ...preferences,
                          allergies: {
                            ...preferences.allergies,
                            [key]: e.target.checked,
                            none: false
                          }
                        });
                      }
                    }}
                  />
                  {label}
                </label>
              ))}
            </div>
          </div>

          <div className="form-group">
            <label>üåô Do you or anyone in your household struggle with:</label>
            <div className="checkbox-group">
              {Object.entries({
                sleep_quality: 'Sleep quality',
                respiratory_issues: 'Respiratory issues',
                asthma: 'Asthma',
                skin_irritation: 'Skin irritation',
                none: 'None'
              }).map(([key, label]) => (
                <label key={key} className="checkbox-label">
                  <input
                    type="checkbox"
                    checked={preferences.health_issues[key]}
                    onChange={(e) => {
                      if (key === 'none') {
                        // If "None" is selected, uncheck all others
                        setPreferences({
                          ...preferences,
                          health_issues: {
                            sleep_quality: false,
                            respiratory_issues: false,
                            asthma: false,
                            skin_irritation: false,
                            none: e.target.checked
                          }
                        });
                      } else {
                        // If any other is selected, uncheck "None"
                        setPreferences({
                          ...preferences,
                          health_issues: {
                            ...preferences.health_issues,
                            [key]: e.target.checked,
                            none: false
                          }
                        });
                      }
                    }}
                  />
                  {label}
                </label>
              ))}
            </div>
          </div>

          <button type="submit" className="save-button">
            Save Preferences
          </button>
        </form>
      </div>
    </div>
  );
};

export default UserPreferences;
