import React, { useState } from 'react';
import { supabase } from './supabaseClient'; // Assuming you already have this set up for Supabase integration

const Questionnaire = ({ user }) => {
  const [selectedCity, setSelectedCity] = useState('');
  const [hasHVAC, setHasHVAC] = useState(false);
  const [hasEcologgica, setHasEcologgica] = useState(false);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    setError(null);

    try {
      // Insert user preferences into Supabase
      const { error } = await supabase.from('user_preferences').insert([
        {
          user_id: user.id, // Assuming 'user' object is passed down with the user ID from Supabase auth
          city: selectedCity,
          has_HVAC: hasHVAC,
          has_ecologgica: hasEcologgica,
        },
      ]);

      if (error) {
        setError('Failed to submit preferences.');
      } else {
        alert('Preferences submitted successfully!');
      }
    } catch (err) {
      setError('Error submitting preferences.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="questionnaire">
      <h1>User Questionnaire</h1>
      <form onSubmit={handleSubmit}>
        {/* City Dropdown */}
        <div>
          <label>
            Where are you located?
            <select value={selectedCity} onChange={(e) => setSelectedCity(e.target.value)}>
              <option value="">Select City</option>
              <option value="Toronto">Toronto</option>
              <option value="New York">New York</option>
              <option value="San Francisco">San Francisco</option>
              <option value="Dallas">Dallas</option>
              <option value="Boston">Boston</option>
              <option value="Miami">Miami</option>
              <option value="Houston">Houston</option>
            </select>
          </label>
        </div>

        {/* HVAC Checkbox */}
        <div>
          <label>
            <input
              type="checkbox"
              checked={hasHVAC}
              onChange={() => setHasHVAC(!hasHVAC)}
            />
            I have an HVAC system
          </label>
        </div>

        {/* Ecologgica Checkbox */}
        <div>
          <label>
            <input
              type="checkbox"
              checked={hasEcologgica}
              onChange={() => setHasEcologgica(!hasEcologgica)}
            />
            I have an Ecologgica product
          </label>
        </div>

        {/* Submit Button */}
        <div>
          <button type="submit" disabled={loading}>
            {loading ? 'Submitting...' : 'Submit'}
          </button>
        </div>

        {/* Error Message */}
        {error && <p style={{ color: 'red' }}>{error}</p>}
      </form>
    </div>
  );
};

export default Questionnaire;
