import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = () => {
    const location = useLocation();

    return (
        <div style={styles.sidebar}>
            <div style={styles.navContent}>
                <Link 
                    to="/dashboard" 
                    style={{
                        ...styles.link,
                        ...(location.pathname === '/dashboard' ? styles.activeLink : {})
                    }}
                >
                    Dashboard
                </Link>
                <Link 
                    to="/anxietydashboard" 
                    style={{
                        ...styles.link,
                        ...(location.pathname === '/anxietydashboard' ? styles.activeLink : {})
                    }}
                >
                    Anxiety Dashboard
                </Link>
                <Link 
                    to="/preferences" 
                    style={{
                        ...styles.link,
                        ...(location.pathname === '/preferences' ? styles.activeLink : {})
                    }}
                >
                    Preferences
                </Link>
            </div>
        </div>
    );
};

const styles = {
    sidebar: {
        backgroundColor: '#D9F6BB',
        height: '100%',
        padding: '20px 0',
        borderRight: '1px solid rgba(4, 58, 36, 0.1)',
        minWidth: '200px',
    },
    navContent: {
        display: 'flex',
        flexDirection: 'column',
        gap: '10px',
    },
    link: {
        color: '#043A24',
        textDecoration: 'none',
        padding: '12px 24px',
        fontSize: '16px',
        transition: 'all 0.3s ease',
        borderRadius: '0 4px 4px 0',
        marginRight: '20px',
        '&:hover': {
            backgroundColor: '#A9ED8A',
            color: '#043A24',
        },
    },
    activeLink: {
        backgroundColor: '#043A24',
        color: '#FFFFFF',
        '&:hover': {
            backgroundColor: '#043A24',
            color: '#FFFFFF',
        },
    },
};

// Add this CSS to your global styles or create a new CSS file
const cssToAdd = `
.active-link {
    background-color: #123522 !important;
    color: #dff5c1 !important;
}

.nav-link:hover {
    background-color: #123522 !important;
    color: #dff5c1 !important;
}
`;

// Add the styles to the document
const styleSheet = document.createElement("style");
styleSheet.innerText = cssToAdd;
document.head.appendChild(styleSheet);

export default Sidebar;
