import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FaHome, FaUser, FaChartBar, FaBook, FaCog } from 'react-icons/fa';

const Sidebar = () => {
    const location = useLocation();

    return (
        <div style={styles.sidebar}>
            <div style={styles.navContent}>
                <Link 
                    to="/dashboard" 
                    style={{
                        ...styles.link,
                        ...(location.pathname === '/dashboard' ? styles.activeLink : {})
                    }}
                >
                    <FaHome style={styles.icon} />
                    Dashboard
                </Link>
                <Link 
                    to="/preferences" 
                    style={{
                        ...styles.link,
                        ...(location.pathname === '/preferences' ? styles.activeLink : {})
                    }}
                >
                    <FaUser style={styles.icon} />
                    Profile
                </Link>
                <Link 
                    to="/insights" 
                    style={{
                        ...styles.link,
                        ...(location.pathname === '/insights' ? styles.activeLink : {})
                    }}
                >
                    <FaChartBar style={styles.icon} />
                    Insights
                </Link>
                <Link 
                    to="/resources" 
                    style={{
                        ...styles.link,
                        ...(location.pathname === '/resources' ? styles.activeLink : {})
                    }}
                >
                    <FaBook style={styles.icon} />
                    Resources
                </Link>
                <Link 
                    to="/settings" 
                    style={{
                        ...styles.link,
                        ...(location.pathname === '/settings' ? styles.activeLink : {})
                    }}
                >
                    <FaCog style={styles.icon} />
                    Settings
                </Link>
            </div>
        </div>
    );
};

const styles = {
    sidebar: {
        backgroundColor: '#043A24',
        height: '100%',
        padding: '20px 0',
        minWidth: '250px',
    },
    navContent: {
        display: 'flex',
        flexDirection: 'column',
        gap: '8px',
    },
    link: {
        color: '#FFFFFF',
        textDecoration: 'none',
        padding: '12px 24px',
        fontSize: '16px',
        transition: 'all 0.3s ease',
        display: 'flex',
        alignItems: 'center',
        gap: '12px',
        '&:hover': {
            backgroundColor: 'rgba(169, 237, 138, 0.1)',
        },
    },
    activeLink: {
        backgroundColor: '#A9ED8A',
        color: '#043A24',
        fontWeight: '500',
        '&:hover': {
            backgroundColor: '#A9ED8A',
        },
    },
    icon: {
        fontSize: '20px',
    },
};

// Add this CSS to your global styles or create a new CSS file
const cssToAdd = `
.active-link {
    background-color: #123522 !important;
    color: #dff5c1 !important;
}

.nav-link:hover {
    background-color: #123522 !important;
    color: #dff5c1 !important;
}
`;

// Add the styles to the document
const styleSheet = document.createElement("style");
styleSheet.innerText = cssToAdd;
document.head.appendChild(styleSheet);

export default Sidebar;
