// UserPreferences.js
import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';
import { useNavigate } from 'react-router-dom';

const UserPreferences = () => {
  const [city, setCity] = useState('');
  const [hasHVAC, setHasHVAC] = useState(false);
  const [hasEcologica, setHasEcologica] = useState(false);
  const navigate = useNavigate();

  // Fetch saved preferences on mount
  useEffect(() => {
    const fetchPreferences = async () => {
      const { data, error } = await supabase
        .from('user_preferences')
        .select('*')
        .single();

      if (data) {
        setCity(data.city);
        setHasHVAC(data.has_HVAC);
        setHasEcologica(data.has_ecologica);
      } else if (error) {
        console.error('Error fetching preferences:', error);
      }
    };

    fetchPreferences();
  }, []);

  const handleSavePreferences = async () => {
    const { error } = await supabase
      .from('user_preferences')
      .upsert({ city, has_HVAC: hasHVAC, has_ecologica: hasEcologica });

    if (error) {
      console.error('Error saving preferences:', error);
    } else {
      alert('Preferences saved successfully!');
      navigate('/dashboard');
    }
  };

  return (
    <div className="preferences-container">
      <h2>Set Your Preferences</h2>
      <div className="form-group">
        <label>Select City:</label>
        <select value={city} onChange={(e) => setCity(e.target.value)}>
          <option value="">Select a city</option>
          <option value="Toronto">Toronto</option>
          <option value="New York">New York</option>
          <option value="San Francisco">San Francisco</option>
          <option value="Dallas">Dallas</option>
          <option value="Boston">Boston</option>
          <option value="Miami">Miami</option>
          <option value="Houston">Houston</option>
        </select>
      </div>
      <div className="checkbox-container">
        <label>
          <input
            type="checkbox"
            checked={hasHVAC}
            onChange={() => setHasHVAC(!hasHVAC)}
          />
          Enable HVAC Adjustment
        </label>
        <label>
          <input
            type="checkbox"
            checked={hasEcologica}
            onChange={() => setHasEcologica(!hasEcologica)}
          />
          Enable Ecologica Adjustment
        </label>
      </div>
      <button onClick={handleSavePreferences}>Save Preferences</button>
    </div>
  );
};

export default UserPreferences;
