import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FaHome, FaUser, FaChartBar, FaBook, FaCog } from 'react-icons/fa';

const Sidebar = () => {
    const location = useLocation();

    return (
        <div className="sidebar">
            <div className="nav-content">
                <Link 
                    to="/dashboard" 
                    className={`nav-link ${location.pathname === '/dashboard' ? 'active' : ''}`}
                >
                    <FaHome />
                    <span>Dashboard</span>
                </Link>
                <Link 
                    to="/preferences" 
                    className={`nav-link ${location.pathname === '/preferences' ? 'active' : ''}`}
                >
                    <FaUser />
                    <span>Profile</span>
                </Link>
                <Link 
                    to="/insights" 
                    className={`nav-link ${location.pathname === '/insights' ? 'active' : ''}`}
                >
                    <FaChartBar />
                    <span>Insights</span>
                </Link>
                <Link 
                    to="/resources" 
                    className={`nav-link ${location.pathname === '/resources' ? 'active' : ''}`}
                >
                    <FaBook />
                    <span>Resources</span>
                </Link>
                <Link 
                    to="/settings" 
                    className={`nav-link ${location.pathname === '/settings' ? 'active' : ''}`}
                >
                    <FaCog />
                    <span>Settings</span>
                </Link>
            </div>
        </div>
    );
};

const styles = {
    sidebar: {
        backgroundColor: '#043A24',
        height: '100%',
        padding: '20px 0',
        minWidth: '250px',
    },
    navContent: {
        display: 'flex',
        flexDirection: 'column',
        gap: '8px',
    },
    link: {
        color: '#FFFFFF',
        textDecoration: 'none',
        padding: '12px 24px',
        fontSize: '16px',
        transition: 'all 0.3s ease',
        display: 'flex',
        alignItems: 'center',
        gap: '12px',
        '&:hover': {
            backgroundColor: 'rgba(169, 237, 138, 0.1)',
        },
    },
    activeLink: {
        backgroundColor: '#A9ED8A',
        color: '#043A24',
        fontWeight: '500',
        '&:hover': {
            backgroundColor: '#A9ED8A',
        },
    },
    icon: {
        fontSize: '20px',
    },
};

// Add this CSS to your global styles or create a new CSS file
const cssToAdd = `
.active-link {
    background-color: #123522 !important;
    color: #dff5c1 !important;
}

.nav-link:hover {
    background-color: #123522 !important;
    color: #dff5c1 !important;
}
`;

// Add the styles to the document
const styleSheet = document.createElement("style");
styleSheet.innerText = cssToAdd;
document.head.appendChild(styleSheet);

export default Sidebar;
