import React, { useState, useEffect } from 'react';
import { supabase } from './supabaseClient';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import 'chartjs-adapter-date-fns';

// Register Chart.js components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  TimeScale
);

ChartJS.defaults.responsive = true;
ChartJS.defaults.maintainAspectRatio = false;

function getLast30Days(data, cityParam) {
    const thirtyDaysAgo = new Date();
    thirtyDaysAgo.setDate(thirtyDaysAgo.getDate() - 30);

    return data.filter(point => {
        const pointDate = new Date(point.date);
        return pointDate >= thirtyDaysAgo && point.city === cityParam;
    });
}

const Dashboard = () => {
  const [airQualityData, setAirQualityData] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [city, setCity] = useState('Toronto');
  const [firstName, setFirstName] = useState('');
  const [hasHVAC, setHasHVAC] = useState(false);
  const [hasEcologica, setHasEcologica] = useState(false);

  useEffect(() => {
    const fetchUserPreferences = async () => {
      try {
        const { data: { user } } = await supabase.auth.getUser();
        if (user) {
          const { data: preferences, error } = await supabase
            .from('user_preferences')
            .select('city, first_name, has_HVAC, has_ecologgica')
            .eq('user_id', user.id)
            .single();

          if (error) throw error;
          if (preferences) {
            setCity(preferences.city);
            setFirstName(preferences.first_name || '');
            setHasHVAC(preferences.has_HVAC || false);
            setHasEcologica(preferences.has_ecologgica || false);
          }
        }
      } catch (error) {
        console.error('Error fetching user preferences:', error);
      }
    };
    fetchUserPreferences();
  }, []);

  useEffect(() => {
    const fetchAirQualityData = async () => {
      try {
        console.log('Fetching data for city:', city);
        const API_URL = 'http://localhost:5000';
        const response = await fetch(`${API_URL}/api/airqualitydata`);

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        console.log('Raw data received:', data.length, 'records');

        const last30DaysData = getLast30Days(data, city);
        console.log('Filtered 30 days data:', last30DaysData.length, 'records');

        setAirQualityData(last30DaysData);
      } catch (error) {
        console.error('Error fetching air quality:', error);
      } finally {
        setIsLoading(false);
      }
    };

    fetchAirQualityData();
  }, [city]);

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: true,
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day'
        },
        grid: {
          display: true,
          color: 'rgba(0, 0, 0, 0.1)',
        }
      },
      y: {
        beginAtZero: true,
        grid: {
          display: true,
          color: 'rgba(0, 0, 0, 0.1)',
        },
        ticks: {
          callback: function(value) {
            return value + ' ug/m続';
          }
        }
      }
    },
    plugins: {
      tooltip: {
        mode: 'index',
        intersect: false,
        callbacks: {
          label: function(context) {
            return `${context.dataset.label}: ${context.parsed.y.toFixed(1)} ug/m続`;
          }
        }
      },
      legend: {
        position: 'bottom',
        labels: {
          usePointStyle: true,
          padding: 15
        }
      }
    }
  };

  return (
    <div className="dashboard">
      <h1>{firstName ? `${firstName}'s ` : ''}{city} Dashboard</h1>
      {isLoading ? (
        <div>Loading...</div>
      ) : (
        airQualityData.length > 0 ? (
          <Line 
            data={{
              labels: airQualityData.map(d => d.date),
              datasets: [
                {
                  label: 'PM2.5',
                  data: airQualityData.map(d => d['PM 2.5']),
                  borderColor: 'rgb(75, 192, 192)',
                  tension: 0.1
                }
              ]
            }} 
            options={chartOptions} 
          />
        ) : (
          <div>No data available for {city}</div>
        )
      )}
    </div>
  );
};

export default Dashboard;
