import React from 'react';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import { Bar } from 'react-chartjs-2';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const CityComparisonChart = ({ data, selectedCity }) => {
  if (!data || data.length === 0) return null;

  // Filter and organize data to ensure selected city is first
  const organizedData = [
    // Find the selected city's data
    ...data.filter(d => d.city === selectedCity),
    // Add other cities' data
    ...data.filter(d => d.city !== selectedCity)
  ];

  const chartData = {
    labels: organizedData.map(d => d.city),
    datasets: [{
      label: 'PM2.5 Level',
      data: organizedData.map(d => d.pm25),
      backgroundColor: organizedData.map(d => 
        d.city === selectedCity ? '#90c789' : '#a7d5a0'
      ),
    }]
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: false
      }
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'PM2.5 Level (μg/m³)'
        }
      }
    }
  };

  return <Bar data={chartData} options={options} />;
};

export default CityComparisonChart;
